import React, { Component } from 'react';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { Redirect } from 'react-router-dom';
import { firestoreConnect } from 'react-redux-firebase';
import TodoListLinks from './TodoListLinks'
import { getFirestore } from 'redux-firestore';

class HomeScreen extends Component {
    handleNewList = () => {
        let newListData = {
            name: 'Unnamed todolist',
            owner: 'Unknown owner',
            items: [],
            time: Date.now(),
        }
        const fireStore = getFirestore();
        let newList = fireStore.collection("todoLists").doc();
        newList.set(newListData);

        this.props.history.push({
            pathname: "todoList/" + newList.id,
            key: newList.id,
        });
    }
    style = {
        marginTop: '100px'
      }
    render() {
        if (!this.props.auth.uid) {
            return <Redirect to="/login" />;
        }

        return (
            <div className="z-depth-2 white" style={{
                paddingBottom: '70px', borderRadius: '0 0 10px 10px',
            }}>
                <div className="dashboard container">
                    <div className="row">
                        <div className="col s12 m4">
                            <TodoListLinks />
                        </div>

                        <div style={this.style} className="waves-effect waves-light col s7 push-s3 pink lighten-1 white-text z-depth-5 hoverable">
                             <h2 className="center-align"><i><u>Wireframer</u></i></h2><br />
                                 <h5 className="center-align">UI Mockup creater</h5>
                         </div>


                        </div>
                    <div className="row">
                             <div style={{ marginTop: '15px' }} className="home_new_list_container ">
                                {/* {<button className="home_new_list_button" onClick={this.handleNewList}>
                                    Create a New To Do List
                                </button>} */}
                                <a onClick={this.handleNewList} className="col s7 push-s3 waves-effect waves-light btn-large red accent-2 hoverable rounded">
                                    <i className="material-icons right">library_add</i>Create a New To Do List
                                </a>
                            </div>
                    </div>
                    </div>
                </div>

        );
    }
}

const mapStateToProps = (state) => {
    return {
        auth: state.firebase.auth
    };
};

export default compose(
    connect(mapStateToProps),
    firestoreConnect([
        { collection: 'todoLists', orderBy: ["time", "desc"] },
    ]),
)(HomeScreen);
