import React, { Component } from 'react';
import { Redirect } from 'react-router-dom'
import { connect } from 'react-redux';
import { compose } from 'redux';

import { firestoreConnect } from 'react-redux-firebase';
import { getFirestore } from 'redux-firestore';
import { Modal, Button } from 'react-materialize';
import { thisExpression } from '@babel/types';

class WireFrameScreen extends Component {
    constructor(props){
        super(props);

        this.currentWidth = 500;
        this.currentHeight = 500;
        if(this.props.wireFrame != null){
            console.log("help!!!!");
            this.currentWidth = this.props.wireFrame.width;
            this.currentHeight = this.props.wireFrame.height;

        }
        this.widthRef = React.createRef();
        this.heightRef = React.createRef();
        this.dimensionsChanged = false;
    }


    state = {
        name: '',
        owner: '',
    }

    sortCriteria = 'none';
    changedTime = false;


    updateDimensions = () => {
        return false;
    }

    updateTime = () => {
        console.log("updating time")
        let fireStore = getFirestore();
        fireStore.collection('wireframes').doc(this.props.wireFrame.id).update({ time: Date.now() })
    }

    handleChange = (e) => {
        this.currentHeight = this.heightRef.current.value;
        this.currentWidth = this.widthRef.current.value;
        this.dimensionsChanged = true;
    }

    addItem = () => {
        console.log("Adding a new item");
        this.props.history.push({
            pathname: this.props.wireFrame.id + "/item/" + this.props.wireFrame.items.length,
        });
    }

    deleteList = () => {
        let fireStore = getFirestore();
        fireStore.collection('wireframes').doc(this.props.wireFrame.id).delete().then(function () {
            console.log("Document successfully deleted!");
        }).catch(function (error) {
            console.error("Error removing document: ", error);
        });

        this.props.history.goBack();
    }

    sortByDescription = () => {
        if (this.sortCriteria !== SORT_BY_TASK_INCREASING)
            this.sortCriteria = SORT_BY_TASK_INCREASING
        else
            this.sortCriteria = SORT_BY_TASK_DECREASING;
        this.sortList(this.sortCriteria);
    }

    sortByDueDate = () => {
        if (this.sortCriteria !== SORT_BY_DUE_DATE_INCREASING)
            this.sortCriteria = SORT_BY_DUE_DATE_INCREASING;
        else
            this.sortCriteria = SORT_BY_DUE_DATE_DECREASING;
        this.sortList(this.sortCriteria);
    }

    sortByCompleted = () => {
        if (this.sortCriteria !== SORT_BY_STATUS_INCREASING)
            this.sortCriteria = SORT_BY_STATUS_INCREASING;
        else
            this.sortCriteria = SORT_BY_STATUS_DECREASING;
        this.sortList(this.sortCriteria);
    }

    sortList = (criteria) => {
        console.log("Sorting by: ", this.sortCriteria);
        let newItems = this.generateItemsInSortedOrder(criteria);
        for (let i = 0; i < newItems.length; i++) {
            newItems[i].key = i;
            newItems[i].id = i;
        }

        let firestore = getFirestore();
        firestore.collection("wireframes").doc(this.props.wireFrame.id).update({ items: newItems });
    }

    generateItemsInSortedOrder = (criteria) => {
        let newItems = Object.assign([], this.props.wireFrame.items);
        newItems.sort(function (a, b) {
            if (criteria === SORT_BY_TASK_INCREASING)
                return a.description.localeCompare(b.description);
            else if (criteria === SORT_BY_TASK_DECREASING)
                return b.description.localeCompare(a.description);
            else if (criteria === SORT_BY_DUE_DATE_INCREASING)
                return a.due_date.localeCompare(b.due_date);
            else if (criteria === SORT_BY_DUE_DATE_DECREASING)
                return b.due_date.localeCompare(a.due_date);
            else if (criteria === SORT_BY_STATUS_INCREASING)
                return ("" + a.completed).localeCompare("" + b.completed);
            else
                return ("" + b.completed).localeCompare("" + a.completed);
        });
        return newItems;
    }

    componentDidMount(){
        if(this.props.wireFrame != null){
            console.log("help!!!!");
            this.widthRef.current.value = this.props.wireFrame.width;
            this.heightRef.current.value = this.props.wireFrame.height;

        }

      console.log(this.props);
    }

    render() {
        const auth = this.props.auth;
        let wireFrame = this.props.wireFrame;
        console.log("here!");
        if (!auth.uid) {
            return <Redirect to="/" />;
        }

        if (!wireFrame)
            return <React.Fragment />

        if (!this.changedTime) {
            this.changedTime = true;
            this.updateTime();
        }

        return (
            <div style={{backgroundColor:  "#473850", marginTop: 10}} className="row lighten-2">
               <div style={{backgroundColor: "#424242"}}className="pull-s1 card col s2 rounded black-text" > 
                    <div style={{padding: 15}} className="card rounded pink lighten-1 white-text">
                        <div style={{margin: 0}}className="row">

                            <div style={{fontSize: "3rem"}}className="col s3 pull-s1 material-icons">zoom_in</div> 
                            <div style={{fontSize: "3rem"}} className="col s3 pull-s1 material-icons">zoom_out</div>
                            <div style={{fontSize: "3rem"}} className="col s3 pull-s1 material-icons">save</div>
                            <div style={{fontSize: "3rem"}} className="col s3 pull-s1 material-icons">close</div>
                        </div>
                    </div>
                    <div className=" card rounded pink lighten-1 white-text">
                        <form style={{marginTop: 10, paddingBottom: 0}}>
                            <div style={{marginBottom: 10}} className="row">
                                <div className="col s2">Width:</div> 
                                <input ref={this.widthRef} onChange={this.handleChange} style={{fontSize: 15, height: 20}}className="white col s6 push-s3" type="number" min="1" max="5000" ></input>
                            </div>
                            <div style={{marginBottom: 10}} className="row">
                                <div className="col s2">Height:</div> 
                                <input ref={this.heightRef} onChange={this.handleChange} style={{fontSize: 15, height: 20}}className="white col s6 push-s3" type="number" min="1" max="5000" ></input>
                            </div>
                            <div className="row">

                                <button style={{marginTop: 10}} type="button" disabled={this.dimensionsChanged}onClick={this.updateDimensions()} className=" col s6 push-s3 btn">Update</button>
                            </div>
                        </form>
                    </div>
                    <div className=" card rounded pink lighten-1 white-text">
                        <div style={{paddingTop: 10, cursor: "pointer"}}className="row">
                            <div style={{height: 40}}className="col s8 push-s2 white white-text rounded"></div>
                            <div className="col s6 push-s3 white-text">Container</div>
                        </div>

                        <div style={{paddingTop: 10, cursor: "pointer"}}className="row">
                            <div className="col s8 push-s2 center-align rounded white black-text">Input Label</div>
                            <div className="col s12 center-align white-text">Label</div>
                        </div>

                        <div style={{paddingTop: 10, cursor: "pointer"}}className="row">
                            <div className="col s8 push-s2 center-align btn white-text">Submit</div>
                            <div className="col s12 center-align white-text">Button</div>
                        </div>

                        <div style={{paddingTop: 10, paddingBottom: 10,  cursor: "pointer"}}className="row">
                            <form style={{marginTop: 0, padding: 0}} >
                                <input style={{fontSize: 15, height: 20}} value="Text Input" type="text" disabled={true} className="col s8 push-s2 center-align white black-text"></input>
                                <div className="col s12 center-align white-text">Textfield</div>
                            </form>
                        </div>
                    </div>
                </div>
               <div style={{marginTop: 7.5, paddingTop: 30, paddingLeft: 30, paddingRight: 30, height: 800, backgroundColor: "#424242"}} className="card col s8 black-text center-align" >
               <div style={{ height: 700, overflow:'auto', backgroundColor: "#424242"}} className="" >
                    <div style={{width: this.width, height: this.height, display: 'block', position: "relative"}} className="white">

                    </div>
                    </div>

               </div>
               <div className="push-s1 card col s2 rounded black-text" > Hello</div>
            </div>
        );
    }
}

const SORT_BY_TASK_INCREASING = 'sort_by_task_increasing';
const SORT_BY_TASK_DECREASING = 'sort_by_task_decreasing';
const SORT_BY_DUE_DATE_INCREASING = 'sort_by_due_date_increasing';
const SORT_BY_DUE_DATE_DECREASING = 'sort_by_due_date_decreasing';
const SORT_BY_STATUS_INCREASING = 'sort_by_status_increasing';
const SORT_BY_STATUS_DECREASING = 'sort_by_status_decreasing';

const mapStateToProps = (state, ownProps) => {
    const { id } = ownProps.match.params;
    const  wireFrames  = state.firestore.data;

    const wireFrameOutline = !!wireFrames ? wireFrames.wireframes : null;
    const wireFrame = !!wireFrameOutline ? wireFrameOutline[id] : null;
    if (wireFrame)
        wireFrame.id = id;



    return {
        wireFrame,
        auth: state.firebase.auth,
    };
};

export default compose(
    connect(mapStateToProps),
    firestoreConnect([
        { collection: 'wireframes' },
    ]),
)(WireFrameScreen);
